import React, { useEffect } from 'react';
import {v4 as uuidv4} from 'uuid';
const Form=({input, setInput, todos, setTodos, editTodo, setEditTodo})=> {
    const updateTodo=(title, id, completed)=>{
        const newTodo=todos.map((todos)=>
        todos.id === id ? {title, id, completed}:todos);
        setTodos(newTodo);
        setEditTodo("");
    };
    
    useEffect(()=>{
        if(editTodo){
            setInput(editTodo.title);
        }

        else{setInput("");}
    }
    );
    const onInputChnage=(event)=>{
        setInput(event.target.value);
    };
    const onFormSubmit=(event)=>{
        event.preventDefault();
        if(!editTodo){
            setTodos([...todos,{id:uuidv4(), title:input, completed: false}]);
            setInput("");
        }
        else{
            updateTodo(input, editTodo.id, editTodo.completed)
        }
    };
    
        return(
            <form onSubmit={onFormSubmit}>
                
                <input type="text" placeholder="Enter a todo..." className="input" value={input} required onFormSubmit={onFormSubmit} onChange={onInputChnage} updateTodo={updateTodo} />
                <input type='number' className='input' value={input} required />

                <button className='button-add' type='Submit' >
                    {editTodo ? "ok" : "Add"}
                </button>
                
            </form>
        );
    };

 
export default Form;
